import React, { useEffect, useState } from "react";
import { getTransactions, addTransaction, deleteTransaction } from "./api";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";

function App() {
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({ title: "", amount: "", type: "expense", category: "" });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const res = await getTransactions();
    setTransactions(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.title || !form.amount || !form.category) return;
    await addTransaction(form);
    setForm({ title: "", amount: "", type: "expense", category: "" });
    fetchData();
  };

  const handleDelete = async (id) => {
    await deleteTransaction(id);
    fetchData();
  };

  // ---- Totals ----
  const totalIncome = transactions
    .filter((t) => t.type === "income")
    .reduce((sum, t) => sum + t.amount, 0);

  const totalExpense = transactions
    .filter((t) => t.type === "expense")
    .reduce((sum, t) => sum + t.amount, 0);

  const balance = totalIncome - totalExpense;

  // ---- Pie chart for Expense categories only ----
  const expenseData = Object.values(
    transactions
      .filter((t) => t.type === "expense")
      .reduce((acc, t) => {
        acc[t.category] = acc[t.category] || { name: t.category, value: 0 };
        acc[t.category].value += t.amount;
        return acc;
      }, {})
  );

  // ---- Income vs Expense Summary ----
  const summaryData = [
    { name: "Income", value: totalIncome },
    { name: "Expense", value: totalExpense },
  ];

  const COLORS = ["#0088FE", "#FF8042", "#00C49F", "#FFBB28", "#FF6384"];

  return (
    <div className="container">
      <h1 className="title">Expense Tracker</h1>

      {/* Summary Cards */}
      <div className="summary">
        <div className="card income">Income: {totalIncome}</div>
        <div className="card expense">Expense: {totalExpense}</div>
        <div className="card balance">Balance: {balance}</div>
      </div>

      {/* Form */}
      <form className="form" onSubmit={handleSubmit}>
        <input
          placeholder="Title"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
        />
        <input
          placeholder="Amount"
          type="number"
          value={form.amount}
          onChange={(e) => setForm({ ...form, amount: Number(e.target.value) })}
        />
        <select
          value={form.type}
          onChange={(e) => setForm({ ...form, type: e.target.value })}
        >
          <option value="expense">Expense</option>
          <option value="income">Income</option>
        </select>
        <input
          placeholder="Category"
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
        />
        <button className="btn" type="submit">Add</button>
      </form>

      {/* Transaction Table */}
      <table className="transaction-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Amount</th>
            <th>Category</th>
            <th>Type</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((t) => (
            <tr key={t._id}>
              <td>{t.title}</td>
              <td className={t.type}>{t.amount}</td>
              <td>{t.category}</td>
              <td>{t.type}</td>
              <td>
                <button className="delete-btn" onClick={() => handleDelete(t._id)}>
                  ‚ùå
                </button>
              </td>
            </tr>
          ))}
        </tbody>
        {/* Totals Row */}
        <tfoot>
          <tr>
            <td colSpan="5" style={{ textAlign: "center", fontWeight: "bold" }}>
              Total Income: <span style={{ color: "green" }}>{totalIncome}</span> | 
              Total Expense: <span style={{ color: "red" }}>{totalExpense}</span> | 
              Balance:{" "}
              <span style={{ color: balance >= 0 ? "green" : "red" }}>
                {balance}
              </span>
            </td>
          </tr>
        </tfoot>
      </table>

      {/* Expense Breakdown */}
      <h2>Category Breakdown (Expenses)</h2>
      <div className="chart">
        <PieChart width={400} height={300}>
          <Pie data={expenseData} dataKey="value" nameKey="name" outerRadius={120}>
            {expenseData.map((entry, index) => (
              <Cell key={index} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </div>

      {/* Income vs Expense Summary */}
      <h2>Income vs Expense</h2>
      <div className="chart">
        <PieChart width={400} height={300}>
          <Pie data={summaryData} dataKey="value" nameKey="name" outerRadius={120}>
            {summaryData.map((entry, index) => (
              <Cell key={index} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </div>
    </div>
  );
}

export default App;
