import React, { useEffect, useState } from "react";
import { getTransactions, addTransaction, deleteTransaction } from "./api";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";

function App() {
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({ title: "", amount: "", type: "expense", category: "" });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const res = await getTransactions();
    setTransactions(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await addTransaction(form);
    setForm({ title: "", amount: "", type: "expense", category: "" });
    fetchData();
  };

  const handleDelete = async (id) => {
    await deleteTransaction(id);
    fetchData();
  };

  // Chart data
  const categoryData = Object.values(transactions.reduce((acc, t) => {
    acc[t.category] = acc[t.category] || { name: t.category, value: 0 };
    acc[t.category].value += t.amount;
    return acc;
  }, {}));

  return (
    <div className="p-6">
      <h1>Expense Tracker</h1>

      <form onSubmit={handleSubmit}>
        <input placeholder="Title" value={form.title} onChange={e => setForm({ ...form, title: e.target.value })} />
        <input placeholder="Amount" type="number" value={form.amount} onChange={e => setForm({ ...form, amount: Number(e.target.value) })} />
        <select value={form.type} onChange={e => setForm({ ...form, type: e.target.value })}>
          <option value="expense">Expense</option>
          <option value="income">Income</option>
        </select>
        <input placeholder="Category" value={form.category} onChange={e => setForm({ ...form, category: e.target.value })} />
        <button type="submit">Add</button>
      </form>

      <ul>
        {transactions.map(t => (
          <li key={t._id}>
            {t.title} - {t.amount} ({t.category}) [{t.type}]
            <button onClick={() => handleDelete(t._id)}>‚ùå</button>
          </li>
        ))}
      </ul>

      <h2>Category Breakdown</h2>
      <PieChart width={400} height={400}>
        <Pie data={categoryData} dataKey="value" nameKey="name" outerRadius={120}>
          {categoryData.map((entry, index) => (
            <Cell key={index} fill={["#0088FE", "#FF8042", "#00C49F", "#FFBB28"][index % 4]} />
          ))}
        </Pie>
        <Tooltip />
        <Legend />
      </PieChart>
    </div>
  );
}

export default App;
