import React, { useEffect, useState } from "react";
import { getTransactions, addTransaction, deleteTransaction } from "./api";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";
import "./App.css";

function App() {
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({ title: "", amount: "", type: "expense", category: "" });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const res = await getTransactions();
    setTransactions(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.title || !form.amount || !form.category) return;
    await addTransaction(form);
    setForm({ title: "", amount: "", type: "expense", category: "" });
    fetchData();
  };

  const handleDelete = async (id) => {
    await deleteTransaction(id);
    fetchData();
  };

  // Totals
  const income = transactions
    .filter((t) => t.type === "income")
    .reduce((acc, t) => acc + t.amount, 0);

  const expenses = transactions
    .filter((t) => t.type === "expense")
    .reduce((acc, t) => acc + t.amount, 0);

  const balance = income - expenses;

  // Chart data
  const categoryData = Object.values(
    transactions.reduce((acc, t) => {
      const cat = t.category || "Uncategorized";
      acc[cat] = acc[cat] || { name: cat, value: 0 };
      acc[cat].value += Number(t.amount);
      return acc;
    }, {})
  );

  return (
    <div className="container">
      <h1 className="title">Expense Tracker</h1>

      {/* Summary Cards */}
      <div className="summary">
        <div className="card income">
          <h3>Income</h3>
          <p>₹{income}</p>
        </div>
        <div className="card expense">
          <h3>Expenses</h3>
          <p>₹{expenses}</p>
        </div>
        <div className="card balance">
          <h3>Balance</h3>
          <p>₹{balance}</p>
        </div>
      </div>

      {/* Add Transaction */}
      <form onSubmit={handleSubmit} className="form">
        <input
          placeholder="Title"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
        />
        <input
          placeholder="Amount"
          type="number"
          value={form.amount}
          onChange={(e) =>
            setForm({ ...form, amount: Number(e.target.value) })
          }
        />
        <select
          value={form.type}
          onChange={(e) => setForm({ ...form, type: e.target.value })}
        >
          <option value="expense">Expense</option>
          <option value="income">Income</option>
        </select>
        <input
          placeholder="Category"
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
        />
        <button type="submit" className="btn">Add</button>
      </form>

      {/* Transactions Table */}
      <table className="transaction-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Amount</th>
            <th>Category</th>
            <th>Type</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map((t) => (
            <tr key={t._id}>
              <td>{t.title}</td>
              <td>₹{t.amount}</td>
              <td>{t.category}</td>
              <td className={t.type}>{t.type}</td>
              <td>
                <button
                  className="delete-btn"
                  onClick={() => handleDelete(t._id)}
                >
                  ❌
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Chart */}
      <h2 className="chart-title">Category Breakdown</h2>
      <div className="chart">
        <PieChart width={400} height={400}>
          <Pie data={categoryData} dataKey="value" nameKey="name" outerRadius={120}>
            {categoryData.map((entry, index) => (
              <Cell
                key={index}
                fill={["#0088FE", "#FF8042", "#00C49F", "#FFBB28", "#A020F0"][index % 5]}
              />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </div>
    </div>
  );
}

export default App;
